INSERT INTO `setting_categories` (`id`, `created`, `modified`, `parent_id`, `name`, `description`) VALUES
(1, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'System', 'Manage site name, contact email, from email, reply to email, captcha type and subscription.'),
(2, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Developments', 'Manage Pre-lauch mode, Maintenance mode, Mobile and touch layout.'),
(3, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'SEO', 'Manage content, meta data and other information relevant to browsers or search engines'),
(4, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Regional, Currency & Language', 'Manage site default language, default city, city routing URL, currency, date-time format and timezone'),
(5, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Account', 'Manage different type of login option such as Facebook, Twitter, Yahoo and Gmail'),
(6, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Deal', 'Manage & configure settings related to deals, sub deal, nearby deal, coupon, commission and deals list options.'),
(7, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Payment', 'Manage different types of "payment gateway" settings of your site, [Wallet, Credit Card Using PayPal, PayPal, Credit Card using Authorize.net CIM, PagSeguro] and <a title="Update Payment Gateway Settings" class="paymentgateway-link" href="##PAYMENT_SETTINGS_URL##">Update Payment Gateway Settings</a>'),
(8, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Charity', 'Manage charity information, commission and withdrawal amount details.'),
(9, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Affiliate', 'Manage affiliate information,  commission and withdrawal amount details.'),
(10, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Referrals', 'Manage referral and various related settings like referral earning type, register cookie expiry time, purchase cookie expiry time, referral amount, referral target count, reward type and refund amount.'),
(11, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Mobile Apps & Push Notification', 'Manage iPhone App key, iPhone push notification settings and Urban Airship settings.<br>\r\nPush notifications allow you to send messages directly to the people who have installed your app, even when the app is closed on a device. \r\nEverytime a deal gets opened, we will send a push notification to iPhone users.'),
(12, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Widget', 'Widgets for deal listing and view page. Widgets can be in iframe and JavaScript embed code, etc (e.g., Twitter Widget, Facebook Like Box, Facebook Feeds Code, Google Ads).'),
(13, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Friends', 'Manage friends request, approve and send mail related settings.'),
(14, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Third Party API', 'Manage third party settings such as Facebook, Twitter, Foursquare, Google Map, Bitly, MailChimp, Gmail, Yahoo, MSN for authentication, shorten URL, sending subscription mail, importing contacts and posting.'),
(15, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'CDN', 'Manage CDN server settings which is used to store the Images, CSS and JavaScript. So all these will be loaded from CDN server to your site.'),
(16, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 'Module Manager', 'Manage Referral, Affiliate, Charity and Friends module enable and disable options.'),
(17, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 'Site Information', 'Here you can modify site related settings such as site name.'),
(18, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 'E-mails', 'Here you can modify email related settings such as contact email, from email, reply-to email.'),
(24, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 4, 'Currency Settings', 'Here you can modify site currency settings such as currency position, default currency and conversion currency.'),
(20, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 'Subscription', 'Here you can manage subscription settings like enabling two step subscription, skip subscription.\r\n##SUBSCRIPTION_CUSTOMIZE## to customize the ''Two Step Subscription Page''.'),
(21, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 2, 'Server', 'Here you can change server settings such as enabling pre-launch screen, maintenance mode, SSL, touch version settings.'),
(22, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 3, 'Metadata', 'Here you can set metadata settings such as meta keyword and description.'),
(23, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 3, 'SEO', 'Here you can set SEO settings such as inserting tracker code and robots.'),
(19, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 4, 'Regional', 'Here you can change regional setting such as site language, default city, routing URL.'),
(25, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 4, 'Date and Time', 'Here you can modify date time settings such as timezone, date time format.'),
(26, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 5, 'Logins', 'Here you can modify user login settings such as enabling 3rd party logins and other login options.'),
(27, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 5, 'Account Settings', 'Here you can modify account settings such as admin approval, email verification, and other site account settings.'),
(28, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 5, 'Privacy', 'Here you can modify privacy settings such as allowing user language change, comments, profile page settings, permission override for user showing name, address, gender and other user personal settings.\r\n'),
(29, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 5, 'Merchant', 'Here you modify merchant settings such as allowing merchant to have normal features, merchant statistics.'),
(30, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 6, 'Configuration', 'Here you modify deal settings such as configuring sub deal, advance payment and other deal related settings.'),
(31, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 6, 'Commission', 'Here you can modify "deal commission" related settings like commission percentage paid to the Merchants, Commission type and other commission related settings for a deal.You can also decide whether the Merchant should be given the permissions to modify the already set commission percentage or should just follow and pay the commission percentage already set by you.'),
(32, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 6, 'Nearby deal', 'Here you can modify nearby deal settings such as nearby deal locator distance and nearby deal setting criteria. Separate section Nearby deal in sidebar shows either Nearby deals based on user current location or user current viewing deal location.'),
(33, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 6, 'Displays', 'Here you can modify deal display related settings such as enabling side deal, deal listing for merchants and other display settings.'),
(34, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 6, 'Coupon', 'Here you can modify coupon related settings such as coupon type, status and other coupon settings.'),
(35, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 6, 'Barcode', 'Here you can modify barcode settings such as symbology, width and height settings. You can enable/disable barcode and its types here. Based on the symbology, the barcode will be displayed in the coupon code. If you use barcode reader in computer, it will help in inserting the coupon code in the text box. If you have POS machine, you can export the coupon code and try matching with that. '),
(36, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 7, 'Deals', 'Here you can modify payment related setting such as auto pay, auto-refund for a deal. Once enabled, no need for administrator to manually close a deal & make payment for the merchant or close the deal when it fails. It will be automatically processed.'),
(37, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 7, 'Gift Card', 'Here you can modify payment related settings for gift cards.'),
(38, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 7, 'Wallet', 'Here you can modify wallet related setting such as enabling groupon-like wallet, maximum and minimum funding limit settings.'),
(39, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 7, 'Cash Withdraw', ''),
(40, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 7, 'Cash Withdraw for User', 'Here you can modify cash withdraw settings for a user such as enabling withdrawal and setting withdraw limit'),
(41, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 8, 'Configuration', 'Here you can modify charity related settings'),
(42, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 8, 'Commission', 'Here you can modify charity related settings such as charity choosing feature.'),
(43, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 8, 'Withdrawal', 'Here you can modify charity withdrawal settings such as transaction fee and fee type settings.'),
(44, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 9, 'Configuration', 'Here you can modify affiliate related settings such as enabling affiliate and referral expiry time.'),
(45, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 9, 'Commission', 'Here you can modify all the "Affiliate related commission settings" such as "commission holding" period and "commission pay type" settings.'),
(46, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 9, 'Withdrawal', 'Here you can modify affiliate withdrawal settings such as threshold limit, transaction fee settings.'),
(47, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 10, 'Configuration', 'Here you can modify referral related settings such as enabling referrals, referral expiry time and other referral basic settings. '),
(48, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 10, 'Refer and Get Amount ', 'Here you can modify "Single Purchase Referral" settings which is used for single referral purchase or register. Settings such as cookie expire time, referral amount settings can be set.'),
(49, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 10, 'Multiple Purchase Referral Amount', 'Here you can modify "Multiple Purchase Referral Amount" settings which is used for multiple referral purchases. Settings like number of referrals and referral reward type can be modified.'),
(50, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 11, 'Configuration', 'Here you can modify push notification configuration such as message format settings.'),
(51, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 11, 'Urban Airship', 'Urban Airship provides a suite for messaging and content delivery tools for mobile application development, including Push Notifications. Once registered, API details can be retrieved by using this link. '),
(52, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 12, 'Side Bar', 'Here you can modify widget embed code which will be displayed in homepage.'),
(53, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 13, 'Configuration', 'Here you can modify friend settings such as enabling friend, auto accept and other friendship related settings.'),
(54, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 14, 'Facebook', 'Facebook is used for login and posting message using its account details. \r\nFor doing this, your site must be configured with existing Facebook account.\r\n<a href="http://dev1products.dev.agriya.com/doku.php?id=facebook-setup"> http://dev1products.dev.agriya.com/doku.php?id=facebook-setup </a>'),
(55, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 14, 'Twitter', 'Twitter is used for login and posting message using its account details. \r\nFor doing this, your site must be configured with existing Twitter account.\r\n<a href="http://dev1products.dev.agriya.com/doku.php?id=twitter-setup"> http://dev1products.dev.agriya.com/doku.php?id=twitter-setup </a>'),
(56, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 14, 'Foursquare', 'Foursquare is used for login and posting message using its account details. \r\nFor doing this, your site must be configured with existing Foursquare account.\r\n<a href="http://dev1products.dev.agriya.com/doku.php?id=foursquare-setup"> http://dev1products.dev.agriya.com/doku.php?id=foursquare-setup </a>'),
(57, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 14, 'Google Map', ''),
(58, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 14, 'Bitly', 'Bitly (bit.ly) is a URL shortening service. You can use this service to shorten the URL form for posting in Twitter, Facebook & other share pages. (Since certain sites restrict the total length of the text to be posted). \r\nFor doing this, your site must be configured with existing Bitly account.\r\n<a href="http://dev1products.dev.agriya.com/doku.php?id=bitly-setup"> http://dev1products.dev.agriya.com/doku.php?id=bitly-setup</a>'),
(59, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 14, 'MailChimp', 'MailChimp is a powerful, easy-to-use email marketing service.\r\nYou can this service when sending subscription mail for the users. (Since sending large of mail at a time can affect our server performance). \r\nFor doing this, your site must be configured with existing MailChimp account.\r\n<a href="http://dev1products.dev.agriya.com/doku.php?id=mailchimp-integration"> http://dev1products.dev.agriya.com/doku.php?id=mailchimp-integration</a>. \r\n##MAIL_CHIMP_LINK## to manage the MailChimp Mailing Lists. '),
(60, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 14, 'Yahoo', 'You can use this service for importing contacts from Yahoo for friends invite. \r\nFor doing this, your site must be configured with existing Yahoo account.\r\n <a href="http://dev1products.dev.agriya.com/doku.php?id=yahoo-setup"> http://dev1products.dev.agriya.com/doku.php?id=yahoo-setup </a>'),
(61, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 14, 'Gmail', 'You can use this service for importing contacts from Gmail for friends invite. \r\nFor doing this, your site must be configured with existing Gmail account.\r\n<a href="http://dev1products.dev.agriya.com/doku.php?id=google_setup"> http://dev1products.dev.agriya.com/doku.php?id=google_setup </a>'),
(62, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 14, 'MSN', 'You can use this service for importing contacts from MSN for friends invite. \r\nFor doing this, your site must be configured with existing MSN account.\r\n<a href="http://dev1products.dev.agriya.com/doku.php?id=msn-setup"> http://dev1products.dev.agriya.com/doku.php?id=google_setup </a>'),
(63, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 15, 'Configuration', ''),
(64, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 16, 'Module', 'Here you can modify module settings such as enabling/disabling master modules settings.'),
(65, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 11, 'Mobile Apps', 'All mobile apps will send secret key (hard coded in Mobile App) to fetch the data from server. App''s key should be matched with this value.<br/> Warning: changing this value may break your mobile apps. '),
(66, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 7, 'Cash Withdraw for Merchant', 'Here you can modify cash withdraw settings for a merchant such as enabling withdrawal and setting withdraw limit'),
(68, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 16, 'Configuration', ''),
(69, '2011-10-11 18:55:26', '2011-10-11 18:55:26', 14, 'Google Translations', '<p>You can this service for quick translation to support new languages in ##TRANSLATIONADD##.</p> <p>Note that Google Translate API is now a <a href="http://code.google.com/apis/language/translate/v2/pricing.html" target="_blank">paid service</a>. Getting Api key, refer <a href="http://dev1products.dev.agriya.com/doku.php?id=google-translation-setup" target="_blank">http://dev1products.dev.agriya.com/doku.php?id=google-translation-setup</a>.</p>');
